css:
.auto-cursor {
  cursor: default;
}
.tab[data-type="kite-error-rescue-sidebar"] .icon-info::before {
  color: #6494ed;
}
kite-error-rescue-sidebar .header {
  background: #31363f;
  padding: 20px 20px 20px;
  margin: 0 -20px;
}
kite-error-rescue-sidebar .kite-column:not(.error-rescue-enabled) .content {
  display: none;
}
kite-error-rescue-sidebar .settings-view {
  position: relative;
}
kite-error-rescue-sidebar .settings-view .settings-panel {
  width: 100%;
}
kite-error-rescue-sidebar .settings-view .setting-title,
kite-error-rescue-sidebar .settings-view .form-control {
  font-size: 1em;
}
kite-error-rescue-sidebar .settings-view .control-group.checkbox {
  padding-left: 60px;
  margin-bottom: .5em;
}
kite-error-rescue-sidebar .settings-view .control-group.checkbox:not(.checked) + .control-group.select {
  pointer-events: none;
  opacity: 0.5;
}
kite-error-rescue-sidebar .settings-view input.input-toggle {
  margin-left: -40px;
}
kite-error-rescue-sidebar .settings-view .control-group.select {
  padding-left: 20px;
}
kite-error-rescue-sidebar .settings-view .control-group.select label {
  flex-direction: row;
  display: flex;
  align-items: baseline;
  flex-wrap: wrap;
}
kite-error-rescue-sidebar .settings-view .control-group.select label .setting-title {
  white-space: nowrap;
  margin-right: 5px;
}
kite-error-rescue-sidebar .settings-view .control-group.select label .form-control {
  width: auto;
  flex: 1 1 0;
}
kite-error-rescue-sidebar .content:not(:empty) {
  padding-top: 10px;
}
kite-error-rescue-sidebar code {
  font-family: var(--editor-font-family);
  font-size: var(--editor-font-size);
  line-height: var(--editor-line-height);
}
kite-error-rescue-sidebar h4 {
  font-size: 1em;
  margin-bottom: 0.5em;
  line-height: 1.5;
  text-transform: uppercase;
  letter-spacing: 0.16em;
  font-weight: normal;
  color: rgba(157, 165, 180, 0.6);
  margin-left: -20px;
}
kite-error-rescue-sidebar .diff {
  margin: 10px 0;
  margin-bottom: 20px;
  margin-left: 20px;
}
kite-error-rescue-sidebar .diff kite-links a,
kite-error-rescue-sidebar .diff kite-links a:hover,
kite-error-rescue-sidebar .diff kite-links a:focus,
kite-error-rescue-sidebar .diff kite-links a:active {
  font-size: 1em;
}
kite-error-rescue-sidebar .diff .feedback-actions a {
  white-space: nowrap;
}
kite-error-rescue-sidebar .diff .icon.icon-file-code::before {
  margin-right: 0;
}
kite-error-rescue-sidebar .diff:not(:first-child) .feedback-actions {
  display: none;
  pointer-events: none;
}
kite-error-rescue-sidebar .message,
kite-error-rescue-sidebar .timestamp {
  margin-top: 10px;
}
kite-error-rescue-sidebar .diff-content {
  margin-top: 10px;
  display: block;
  padding: 0;
  border-radius: 0;
  background: transparent;
}
kite-error-rescue-sidebar .feedback-actions {
  display: flex;
  text-align: left;
  margin-top: 10px;
}
kite-error-rescue-sidebar .feedback-actions a {
  color: rgba(157, 165, 180, 0.6);
}
kite-error-rescue-sidebar .feedback-actions a:hover {
  color: #d7dae0;
}
kite-error-rescue-sidebar .feedback-actions a.clicked {
  color: white;
  opacity: 1;
}
kite-error-rescue-sidebar .feedback-feedback {
  display: block;
  min-height: 2em;
  margin-top: 0.5em;
  padding-top: 0.5em;
  border-top: 1px solid transparent;
  color: transparent;
}
kite-error-rescue-sidebar .feedback-feedback.hint {
  color: rgba(157, 165, 180, 0.6);
  border-top-color: #31363f;
}
kite-error-rescue-sidebar .feedback-feedback.confirmation {
  color: #d7dae0;
  border-top-color: #31363f;
}
kite-error-rescue-sidebar del,
kite-error-rescue-sidebar ins {
  display: flex;
  flex-direction: row;
  text-decoration-line: none;
  border: 1px solid #31363f;
}
kite-error-rescue-sidebar del .line-number,
kite-error-rescue-sidebar ins .line-number {
  width: 80px;
  opacity: 0.5;
  padding: 4px 10px;
  color: #9da5b4;
  border-right: 1px solid #31363f;
}
kite-error-rescue-sidebar del strong,
kite-error-rescue-sidebar ins strong {
  padding: 0.2em 0.1em;
  margin: -0.2em -0.1em;
  font-weight: inherit;
}
kite-error-rescue-sidebar del .line,
kite-error-rescue-sidebar ins .line {
  padding: 4px;
  padding-left: 30px;
  position: relative;
  color: #abb2bf;
  white-space: pre-wrap;
  word-break: break-word;
  margin-left: 2ch;
  text-indent: -2ch;
}
kite-error-rescue-sidebar del .line::before,
kite-error-rescue-sidebar ins .line::before {
  opacity: 0.5;
  position: absolute;
  left: 0;
  padding-left: 10px;
}
kite-error-rescue-sidebar ins {
  border-top: 0;
}
kite-error-rescue-sidebar del .line::before {
  content: '+';
}
kite-error-rescue-sidebar ins .line::before {
  content: '-';
}
kite-error-rescue-sidebar ins .line-number {
  text-align: right;
}
kite-error-rescue-sidebar del strong {
  border: 1px solid rgba(224, 82, 82, 0.3);
}
kite-error-rescue-sidebar ins strong {
  border: 1px solid rgba(67, 208, 138, 0.3);
}
kite-error-rescue-sidebar .recent del {
  border: 0;
  margin: 0;
  background-color: #4f2635;
}
kite-error-rescue-sidebar .recent del .line-number {
  background-color: #642732;
  border: none;
}
kite-error-rescue-sidebar .recent del strong {
  background-color: #792932;
  border: none;
}
kite-error-rescue-sidebar .recent ins {
  border: 0;
  margin: 0;
  background-color: #215049;
}
kite-error-rescue-sidebar .recent ins .line-number {
  background-color: #22624e;
  border: none;
}
kite-error-rescue-sidebar .recent ins strong {
  background-color: #227456;
  border: none;
}
kite-error-rescue-sidebar a.icon-question {
  position: absolute;
  top: 0.4em;
  right: 0;
  z-index: 2;
  font-size: 1.5em;
  color: rgba(157, 165, 180, 0.6);
}
kite-error-rescue-sidebar a.icon-question:hover::before {
  color: #6494ed;
}
kite-error-rescue-sidebar a.icon-question::before {
  font-size: 1em;
  width: auto;
  height: auto;
  margin: 0;
}
kite-error-rescue-sidebar .messages.has-messages + div .icon-question {
  display: none;
}
kite-error-rescue-sidebar .message-box {
  margin: 0 0 20px;
  padding: 20px 60px 20px 20px;
  border-radius: 3px;
  background: #21252b;
  position: relative;
}
kite-error-rescue-sidebar .message-box h4 {
  margin-top: 0;
  margin-left: 0;
  margin-bottom: 10px;
}
kite-error-rescue-sidebar .message-box .btn-close {
  -webkit-appearance: none;
  background: transparent;
  border: none;
  padding: 0.5em;
  margin: calc(3.5px);
  position: absolute;
  right: 10px;
  top: 10px;
  color: rgba(157, 165, 180, 0.6);
}
kite-error-rescue-sidebar .message-box .btn-close .icon::before {
  margin-right: 0;
}
kite-error-rescue-sidebar .message-box .btn-close:hover {
  color: #d7dae0;
}
kite-error-rescue-sidebar .message-box p + .diff-content {
  margin-top: -10px;
}
kite-error-rescue-sidebar .message-box .diff-content {
  margin-bottom: 20px;
}
kite-error-rescue-sidebar .message-box a {
  color: #6494ed;
}
kite-error-rescue-sidebar .kite-column {
  display: block !important;
  overflow-y: auto !important;
  padding: 0 20px !important;
}
kite-error-rescue-sidebar footer {
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  flex: 0 0 auto;
  z-index: 1000;
  height: 4em;
  line-height: 4em;
  color: rgba(157, 165, 180, 0.6);
  padding: 0 20px;
  border-top: none;
  border-bottom: none;
  position: absolute;
  bottom: 0;
  right: 0;
  box-shadow: 0 -3px 6px #21252b;
}
