css:
.expand-at-cursor-highlight {
  pointer-events: none;
}
.expand-at-cursor-highlight.region {
  border-radius: 3px;
  background-color: rgba(62, 68, 81, 0.5);
  background-image: linear-gradient(-0.2turn, transparent 20%, #3e4451 50%, transparent 80%);
  background-size: 2em 2em;
  background-position: -2em 0;
  background-repeat: no-repeat;
  box-sizing: content-box !important;
  margin-left: -5px;
  padding-right: 10px;
  animation: expand-at-cursor-highlight-disappearing 600ms ease-out;
  animation-fill-mode: both;
}
@keyframes expand-at-cursor-highlight-disappearing {
  50%,
  100% {
    background-position-x: calc(100% + 2em);
  }
  100% {
    opacity: 0;
  }
}
.expand-at-cursor-highlight.error.region {
  background-color: rgba(209, 59, 46, 0.34);
  background-image: linear-gradient(-0.2turn, transparent 20%, rgba(209, 59, 46, 0.67) 50%, transparent 80%);
}
