css:
.auto-cursor {
  cursor: default;
}
atom-panel.left kite-error-rescue-sidebar .kite-column,
atom-panel.left kite-sidebar .kite-column {
  padding-right: 5px;
}
atom-panel.right kite-error-rescue-sidebar .kite-column,
atom-panel.right kite-sidebar .kite-column {
  padding-left: 5px;
}
kite-error-rescue-sidebar,
kite-sidebar {
  display: flex;
  font-size: 1em;
  font-family: system-ui;
  flex-direction: row;
  background: #21252b;
}
kite-error-rescue-sidebar h4 .sticky.fixed,
kite-sidebar h4 .sticky.fixed {
  background: #21252b;
}
kite-error-rescue-sidebar h4 .sticky.fixed:hover,
kite-sidebar h4 .sticky.fixed:hover {
  background: #28303f;
}
kite-error-rescue-sidebar .debug,
kite-sidebar .debug {
  background: #282c34;
}
kite-error-rescue-sidebar .btn.icon-x,
kite-sidebar .btn.icon-x {
  border: 1px solid transparent;
  background: transparent;
  transition: border 0.2s, background 0.2s;
}
kite-error-rescue-sidebar .kite-column,
kite-sidebar .kite-column {
  display: flex;
  padding: 0 0;
  flex-direction: column;
  order: 1;
  flex: 1 1 auto;
  overflow: hidden;
}
kite-error-rescue-sidebar .background-message,
kite-sidebar .background-message {
  opacity: 0.5;
}
kite-error-rescue-sidebar .background-message kite-logo,
kite-sidebar .background-message kite-logo {
  display: block;
}
kite-error-rescue-sidebar .background-message kite-logo svg,
kite-sidebar .background-message kite-logo svg {
  width: 2em;
}
kite-error-rescue-sidebar .background-message kite-logo svg polygon,
kite-sidebar .background-message kite-logo svg polygon {
  fill: #9da5b4;
}
kite-error-rescue-sidebar kite-navigable-stack,
kite-sidebar kite-navigable-stack,
kite-error-rescue-sidebar kite-navigable-stack-breadcrumb,
kite-sidebar kite-navigable-stack-breadcrumb {
  flex: 1 1 0;
}
kite-error-rescue-sidebar kite-navigable-stack .expand-header,
kite-sidebar kite-navigable-stack .expand-header,
kite-error-rescue-sidebar kite-navigable-stack-breadcrumb .expand-header,
kite-sidebar kite-navigable-stack-breadcrumb .expand-header,
kite-error-rescue-sidebar kite-navigable-stack .scroll-wrapper .sections-wrapper,
kite-sidebar kite-navigable-stack .scroll-wrapper .sections-wrapper,
kite-error-rescue-sidebar kite-navigable-stack-breadcrumb .scroll-wrapper .sections-wrapper,
kite-sidebar kite-navigable-stack-breadcrumb .scroll-wrapper .sections-wrapper {
  padding: 0 20px;
  padding-top: 10px;
}
kite-error-rescue-sidebar kite-navigable-stack .sections-wrapper section.summary:empty,
kite-sidebar kite-navigable-stack .sections-wrapper section.summary:empty {
  display: none;
}
kite-error-rescue-sidebar header,
kite-sidebar header {
  display: flex;
  flex-direction: row;
  justify-content: flex-end;
}
kite-error-rescue-sidebar header kite-navigable-stack-breadcrumb,
kite-sidebar header kite-navigable-stack-breadcrumb {
  align-items: center;
}
kite-error-rescue-sidebar footer,
kite-sidebar footer {
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  flex: 0 0 auto;
  z-index: 1000;
  height: 4em;
  line-height: 4em;
  color: rgba(157, 165, 180, 0.6);
  padding: 0 20px;
  box-shadow: 0 -3px 6px #21252b;
}
kite-error-rescue-sidebar footer .actions a,
kite-sidebar footer .actions a {
  color: rgba(157, 165, 180, 0.6);
  text-decoration-line: none;
}
kite-error-rescue-sidebar footer .actions a:hover,
kite-sidebar footer .actions a:hover {
  text-decoration-line: none;
}
kite-error-rescue-sidebar footer .actions a span,
kite-sidebar footer .actions a span {
  text-decoration: underline;
}
kite-error-rescue-sidebar footer .actions a + a:before,
kite-sidebar footer .actions a + a:before {
  content: ' Â· ';
  padding: 0 0.2em;
  color: rgba(157, 165, 180, 0.6);
}
kite-error-rescue-sidebar footer .actions > *,
kite-sidebar footer .actions > * {
  display: none;
}
kite-error-rescue-sidebar footer kite-logo.badge,
kite-sidebar footer kite-logo.badge {
  margin-top: 2em;
}
kite-error-rescue-sidebar .has-scroll .actions > *,
kite-sidebar .has-scroll .actions > * {
  display: inline-block;
}
kite-sidebar footer {
  border-top: 1px solid #31363f;
  border-bottom: 1px solid #31363f;
}
