css:
.auto-cursor {
  cursor: default;
}
kite-tour {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-around;
  font-size: 1.2rem;
  background: #282c34;
}
kite-tour p {
  margin-bottom: 10px;
}
kite-tour a,
kite-tour a:visited,
kite-tour a:active,
kite-tour a:focus,
kite-tour a:hover {
  color: #6494ed;
}
kite-tour h5 {
  font-size: 1.2em;
  margin-top: 20px;
}
kite-tour h5 kite-logo {
  margin-right: 10px;
}
kite-tour h5 span,
kite-tour h5 kite-logo {
  display: inline-block;
  vertical-align: middle;
}
kite-tour kite-logo svg {
  height: 1.2em;
}
kite-tour kite-logo svg polygon {
  fill: #d7dae0;
}
kite-tour section {
  display: flex;
  width: 640px;
  max-width: 100%;
  flex-direction: row;
  align-items: center;
  padding: 0 20px;
}
kite-tour section + section {
  margin-top: 20px;
}
kite-tour section + p {
  margin-top: 20px;
}
kite-tour section figure {
  flex: 0 0 auto;
  width: 220px;
  background: #3a3f4b;
  margin-right: 30px;
}
kite-tour section figure img {
  width: 100%;
}
kite-tour .control-group {
  line-height: 1em;
}
kite-tour .kite-tour-scroll-section {
  flex: 1 1 auto;
  overflow-y: scroll;
  margin: 10px 0;
}
kite-tour .kite-tour-scroll-section p {
  text-align: center;
}
kite-tour .kite-tour-scroll-section section p {
  text-align: left;
}
kite-tour .kite-tour-gutter {
  max-width: 100%;
  width: 600px;
  display: flex;
  flex-direction: column;
  align-items: center;
}
