css:
.auto-cursor {
  cursor: default;
}
div.kite.inline {
  display: inline-block;
}
div.kite.row {
  display: flex;
  flex-direction: row;
}
div.kite.row.vertical-align {
  align-items: center;
}
.install-flow {
  color: #d7dae0;
  font-weight: 200;
  margin: 0 auto;
  overflow: auto;
  max-width: 800px;
  min-width: 640px;
  width: 85%;
  /* A/B testing stylings */
}
.install-flow.disabled {
  pointer-events: none;
}
.install-flow.disabled .ignore-disabled {
  pointer-events: initial;
}
.install-flow input[type=text],
.install-flow input[type=email],
.install-flow input[type=password] {
  background-color: transparent;
  border: 1px solid #d7dae0;
  border-radius: 0.3rem;
  display: block;
  font-size: 1.2rem;
  font-weight: 100;
  margin-bottom: 1rem;
  opacity: 0.8;
  padding: 0.5rem 0.75rem;
}
.install-flow p {
  font-size: 1rem;
  margin: 1rem 0;
}
.install-flow p.ending-paragraph {
  margin-bottom: 2.5rem;
}
.install-flow p.strong {
  font-size: 1.1rem;
  font-weight: 600;
}
.install-flow p a {
  text-decoration: underline;
}
.install-flow ul,
.install-flow ul li {
  font-size: 1rem;
}
.install-flow .title {
  font-size: 1.2rem;
  padding: 1.5rem 0.5rem 0.5rem;
  text-align: center;
}
.install-flow .cta-btn {
  border: none;
  border-radius: 0.3rem;
  font-size: 1.1rem;
  padding: 0.5rem 2rem;
}
.install-flow .separator {
  padding: 1.5rem 1rem;
}
.install-flow .separator .separator-border {
  border-bottom: 1px solid rgba(215, 218, 224, 0.007);
  width: 40%;
}
.install-flow .separator .separator-text {
  font-size: 1.4rem;
  opacity: 0.7;
  text-align: center;
  width: 20%;
}
.install-flow[data-install-copy="long"] div[data-install-copy="short"] {
  display: none;
}
.install-flow[data-install-copy="short"] div[data-install-copy="long"] {
  display: none;
}
.install-flow[data-install-title="thankyou"] .title:before {
  content: 'Choose your autocomplete-python engine';
}
.install-flow[data-install-title="choose"] .title:before {
  content: 'Choose your autocomplete-python engine';
}
.install-flow .logo {
  color: #d7dae0;
  background-image: none !important;
}
.install-flow[data-button-position="top"] .cta-btn.bottom {
  display: none;
}
.install-flow[data-button-position="top"] .cta-btn.top {
  display: block;
}
.install-flow[data-button-position="bottom"] .cta-btn.top {
  display: none;
}
.install-flow[data-button-position="bottom"] .cta-btn.bottom {
  display: block;
}
.install-flow[data-show-screenshot="no"] .screenshot {
  display: none;
}
.install-flow .install-flow-step {
  padding: 1.5rem 2rem;
}
.install-flow .install-flow-step .status {
  font-size: 1.1rem;
  margin: 1rem 0;
  text-align: center;
}
.install-flow .install-flow-step .status.error {
  color: #e05252;
}
.install-flow .install-flow-step .secondary-cta-section {
  padding: 1rem 0.2rem;
}
.install-flow .install-flow-step .secondary-cta-section a {
  color: rgba(215, 218, 224, 0.005);
  font-size: 1.1rem;
}
.install-flow .install-flow-step .secondary-cta-section a:hover {
  text-decoration: none;
}
.install-flow .install-flow-step .secondary-cta-section a:last-child {
  float: right;
}
.install-flow .install-flow-step.install-step .comparison {
  align-items: flex-start;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
}
.install-flow .install-flow-step.install-step .section {
  padding: 1rem;
}
.install-flow .install-flow-step.install-step .highlighted-section {
  border: 1px solid #d7dae0;
  border-radius: 1rem;
  background-color: rgba(51, 160, 255, 0.003);
  height: 400px;
  width: 48%;
}
.install-flow .install-flow-step.install-step .features {
  height: 140px;
}
.install-flow .install-flow-step.install-step .features .feature {
  display: flex;
  flex-direction: row;
  padding: 0.3rem 0;
}
.install-flow .install-flow-step.install-step .separator {
  background: #181a1f;
  height: 2px;
  margin: 1rem auto;
  padding: 0;
  width: 100%;
}
.install-flow .install-flow-step.install-step .localities .locality {
  display: flex;
  flex-direction: row;
  padding: 0.3rem 0;
}
.install-flow .install-flow-step.install-step .localities .locality .description {
  font-size: 1.1rem;
}
.install-flow .install-flow-step.install-step .localities .locality .description a {
  text-decoration: underline;
}
.install-flow .install-flow-step.install-step .cta-row {
  bottom: 1.5rem;
  left: 0;
  position: absolute;
  right: 0;
}
.install-flow .install-flow-step.install-step .cta-btn {
  background-color: #43d08a;
  width: 75%;
}
.install-flow .install-flow-step.install-step .cta-btn.secondary-cta {
  background-color: rgba(255, 255, 255, 0.0015);
  padding-left: 2rem;
  padding-right: 2rem;
}
.install-flow .install-flow-step.install-step .cta-btn.centered {
  margin: 0 auto;
  text-align: center;
}
.install-flow .install-flow-step.install-step .row.title-row,
.install-flow .install-flow-step.install-step .row.loading-row {
  justify-content: space-between;
  padding-bottom: 1rem;
  align-items: center;
  align-content: center;
}
.install-flow .install-flow-step.install-step .row.title-row .logo,
.install-flow .install-flow-step.install-step .row.loading-row .logo {
  background-repeat: no-repeat;
  background-size: contain;
  width: 100px;
  font-size: 1.2rem;
}
.install-flow .install-flow-step.install-step .row.title-row .logo.kite,
.install-flow .install-flow-step.install-step .row.loading-row .logo.kite {
  background-image: url('atom://kite/node_modules/kite-installer/styles/smalllogo.png');
}
.install-flow .install-flow-step.install-step .row.title-row .tagline,
.install-flow .install-flow-step.install-step .row.loading-row .tagline {
  font-size: 1.1rem;
}
.install-flow .install-flow-step.install-step .row.title-row .label,
.install-flow .install-flow-step.install-step .row.loading-row .label {
  font-size: 1.2rem;
}
.install-flow .install-flow-step.install-step .row.title-row .separator,
.install-flow .install-flow-step.install-step .row.loading-row .separator {
  flex-grow: 1;
}
.install-flow .install-flow-step.install-step .row.loading-row {
  font-size: 1.1rem;
  margin: 0 auto;
  width: 400px;
}
.install-flow .install-flow-step.install-step .row.loading-row .loading-message {
  align-items: center;
}
.install-flow .install-flow-step.install-step .progress-bar {
  height: 2.75rem;
  position: relative;
}
.install-flow .install-flow-step.install-step .progress-bar .progress-bar-status {
  font-size: 1.2rem;
  height: 2.75rem;
  line-height: 2.75rem;
  position: relative;
  text-align: center;
  width: 100%;
  z-index: 2;
}
.install-flow .install-flow-step.install-step .progress-bar .progress-bar-progress {
  bottom: 0;
  height: 2.75rem;
  left: 0;
  position: absolute;
  z-index: 1;
}
.install-flow .install-flow-step.install-step .screenshot {
  background-image: url('atom://kite/node_modules/kite-installer/styles/screenshot.png');
  height: 186px;
  max-width: 300px;
  width: 95%;
  background-size: contain;
  background-position: center center;
  background-repeat: no-repeat;
  margin: 0 auto 1rem;
  padding: 0;
}
.install-flow .install-flow-step.create-account-step,
.install-flow .install-flow-step.login-step,
.install-flow .install-flow-step.whitelist-step {
  margin: 0 auto;
  width: 360px;
}
.install-flow .install-flow-step.create-account-step input[type=text],
.install-flow .install-flow-step.login-step input[type=text],
.install-flow .install-flow-step.whitelist-step input[type=text],
.install-flow .install-flow-step.create-account-step input[type=email],
.install-flow .install-flow-step.login-step input[type=email],
.install-flow .install-flow-step.whitelist-step input[type=email],
.install-flow .install-flow-step.create-account-step input[type=password],
.install-flow .install-flow-step.login-step input[type=password],
.install-flow .install-flow-step.whitelist-step input[type=password] {
  width: 100%;
}
.install-flow .install-flow-step.create-account-step .cta-btn,
.install-flow .install-flow-step.login-step .cta-btn,
.install-flow .install-flow-step.whitelist-step .cta-btn {
  background-color: #33a0ff;
  padding: 0.5rem;
  width: 100%;
}
.install-flow .welcome-to-kite p,
.install-flow .welcome-to-kite ul,
.install-flow .welcome-to-kite li {
  font-size: 1rem;
}
.install-flow .welcome-to-kite .welcome-title {
  display: flex;
  justify-content: space-between;
  align-items: center;
  align-content: center;
}
.install-flow .welcome-to-kite .welcome-title h3 {
  font-weight: normal;
  margin: 0;
  font-size: 1.4rem;
}
.install-flow .welcome-to-kite .welcome-title .title-logo {
  width: 25px;
  height: 32px;
  background-image: url('atom://kite/node_modules/kite-installer/styles/smalllogo.png');
  background-repeat: no-repeat;
  background-size: contain;
  flex-grow: 0;
  flex-shrink: 0;
}
.install-flow .welcome-to-kite .warning {
  background-color: rgba(215, 218, 224, 0.0005);
  padding: 1rem;
  font-size: 1rem;
  color: rgba(215, 218, 224, 0.006);
}
.install-flow .welcome-to-kite .description {
  display: flex;
  justify-content: space-between;
  margin: 1rem 0;
  align-items: center;
  align-content: center;
}
.install-flow .welcome-to-kite .description .description-screenshot {
  width: 183px;
  height: 200px;
  background-image: url('atom://kite/node_modules/kite-installer/styles/plotscreenshot.png');
  background-repeat: no-repeat;
  background-size: contain;
  flex-grow: 0;
  flex-shrink: 0;
}
.install-flow .welcome-to-kite p.feedback {
  border-top: 3px solid rgba(215, 218, 224, 0.0005);
  font-size: 0.9rem;
  padding-top: 1rem;
  color: rgba(215, 218, 224, 0.006);
}
.modal-metrics-row {
  margin: 0 -5px;
  overflow: hidden;
}
.modal-metrics-row .modal-metrics-cell {
  padding: 0 5px;
  width: 50%;
  float: left;
}
.install-flow .title::before {
  content: none !important;
}
.icon-kite::before {
  content: '';
  display: inline-block;
  vertical-align: middle;
  width: 16px;
  height: 16px;
  mask-image: url('atom://kite/assets/logo-no-text.svg');
  mask-size: contain;
  mask-repeat: no-repeat;
  -webkit-mask-image: url('atom://kite/assets/logo-no-text.svg');
  -webkit-mask-repeat: no-repeat;
  -webkit-mask-size: contain;
  background-color: #d7dae0;
}
.icon-so::before {
  content: '';
  display: inline-block;
  vertical-align: middle;
  font-size: 1.2em;
  line-height: 1em;
  height: 1em;
  width: 1em;
  background-image: url('atom://kite/assets/so.svg');
}
.icon-kite-gift::before {
  content: '';
  display: inline-block;
  vertical-align: middle;
  font-size: 1.2em;
  line-height: 1em;
  height: 1em;
  width: 1em;
  background-size: 100%;
}
.platform-darwin .icon-kite-gift::before {
  background-image: url('atom://kite/assets/icon-gift.png');
}
.platform-win32 .icon-kite-gift::before {
  background-image: url('atom://kite/assets/icon-gift-windows.png');
}
.tooltip.kite-tooltip .tooltip-arrow {
  border-top-color: #3a3f4b !important;
}
.tooltip.kite-tooltip .tooltip-inner {
  background: #3a3f4b;
}
.btn.btn-purple {
  color: #d7dae0;
  text-shadow: none;
  border: 1px solid #181a1f;
  background-color: #9B92FB;
  background-image: linear-gradient(#9a91fb, #9B92FB);
  color: white;
}
atom-text-editor .kite-hyperclick .region {
  z-index: 1;
  pointer-events: auto;
  border-bottom: 1px solid #6494ed;
  cursor: pointer;
}
.flex-separator {
  flex-grow: 1;
}
